mapper.select:
select
       s.shop_id,s.shop_name,s.shop_desc,s.shop_addr,s.phone,s.shop_img,s.priority,s.create_time,s.last_edit_time,s.enable_status,s.advice,
       a.area_id,a.area_name,
       sc.shop_category_id,sc.shop_category_name
from tb_shop s,tb_area a,tb_shop_category sc


        and s.shop_category_id=#{shopCondition.shopCategory.shopCategoryId}


        and s.area_id=#{shopCondition.area.areaId}


        and s.shop_name like '%${shopCondition.shopName}%'


        and s.enable_status=#{shopCondition.enableStatus}


        and s.owner_id=#{shopCondition.owner.userId}

    and s.area_id=a.area_id and s.shop_category_id=sc.shop_category_id

order by s.priority desc
limit #{rowIndex}, #{pageSize}

select count(*)
  from tb_shop s,tb_area a,tb_shop_category sc


          and s.shop_category_id=#{shopCondition.shopCategory.shopCategoryId}


          and s.area_id=#{shopCondition.area.areaId}


          and s.shop_name like '%${shopCondition.shopName}%'


          and s.enable_status=#{shopCondition.enableStatus}


          and s.owner_id=#{shopCondition.owner.userId}

      and s.area_id=a.area_id and s.shop_category_id=sc.shop_category_id

mapper.update:
update tb_shop
shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_Img=#{shopImg}, advice=#{advice}, area_id=#{area.areaId}, last_edit_time=#{lastEditTime}, shop_category_id=#{shopCategory.shopCategoryId}
where shop_id=#{shopId}
